        public static int ProcessDay1(out int basement)
        {
            int floor = 0;
            bool found = false;
            int basementCount = 0;
            basement = 0;

            foreach (char c in data)
            {
                basementCount++;

                if (c == '(')
                {
                    floor++;
                }
                else if (c == ')')
                {
                    floor--;

                    if (floor == -1 && !found)
                    {
                        basement = basementCount;
                        found = true;
                    }
                }
            }

            return floor;
        }
